mapper.insert:
INSERT INTO t_company (
    INDU_CATE2_ID
    , COMP_TYPE
    , COMP_NM
    , COMP_LOGO_IMAGE_NM
    , COMP_BIRTH_DT
    , COMP_CEO
    , COMP_ADDR
    , COMP_PHONE
    , COMP_HOMEPAGE
    , COMP_SALES
    , COMP_REG_DT
    , COMP_UDT_DT
    , ADMIN_ID
) VALUES (
    #{industryCategory2Id}
    , #{companyType}
    , #{companyName}
    , #{companyLogoImageName}
    , #{companyBirthDate}
    , #{companyCeo}
    , #{companyAddress}
    , #{companyPhone}
    , #{companyHomePage}
    , #{companySales}
    , NOW()
    , NOW()
    , #{adminId}
)

mapper.update:
UPDATE t_company
SET
    INDU_CATE2_ID         = #{industryCategory2Id}
    , COMP_TYPE           = #{companyType}
    , COMP_NM             = #{companyName}
    , COMP_LOGO_IMAGE_NM  = #{companyLogoImageName}
    , COMP_BIRTH_DT       = #{companyBirthDate}
    , COMP_CEO            = #{companyCeo}
    , COMP_ADDR           = #{companyAddress}
    , COMP_PHONE          = #{companyPhone}
    , COMP_HOMEPAGE       = #{companyHomePage}
    , COMP_SALES          = #{companySales}
    , COMP_UDT_DT         = NOW()
    , ADMIN_ID            = #{adminId}
WHERE
    COMP_ID = #{companyId}
